import React, {useState} from "react";
import { Button, Form } from "react-bootstrap";

const SignUp = () => {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const submitForm = () => {
        console.log("Form submitted")
    }


    return (
        <div className="container">
            <div className="form">
                <h1>Sign Up</h1>
                <form>
                    <Form.Group>
                        <Form.Label>Username</Form.Label>
                        <Form.Control type="text" placeholder="Enter username" value={username} name="username" onChange={(e) => {setUsername(e.tar)}}/>
                    </Form.Group>
                    <br />
                    <Form.Group>
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" value={email} name="email" />
                    </Form.Group>
                    <br />
                    <Form.Group>
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" value={password} name="password" />
                    </Form.Group>
                    <br />
                    <Form.Group>
                        <Form.Label>Confirm Password</Form.Label>
                        <Form.Control type="password" placeholder="Confirm password" value={confirmPassword} name="confirmPassword" />
                    </Form.Group>
                    <br />
                    <Form.Group>
                        <Button as="sub" variant="primary" type="submit" onClick={submitForm}>Sign Up</Button>
                    </Form.Group>
                </form>
            </div>
        </div>
    );
}

export default SignUp;
